con:eventHandlers.con:script:
// Sample event script to add custom HTTP header to all outgoing REST, SOAP and HTTP(S) calls
// This code is often used for adding custom authentication to SoapUI Pro tests

// If hardcoding the token, uncomment and change line 5
// token = '4567'

// If your token is parameterized in Project level custom property, uncomment line 8
// token = request.parent.testCase.testSuite.project.getProperty('auth_token').getValue()

// To modify all outgoing calls, remove comments from lines 11 to 16
// headers = request.requestHeaders
// if (headers.containsKey('auth_token2') == false) {
//   headers.put('auth_token2', token)
//   request.requestHeaders = headers
// }

import java.io.FileWriter;
import au.com.bytecode.opencsv.CSVWriter

def outputFilePath = 'C:\\logs\\test.csv'
CSVWriter writer = new CSVWriter(new FileWriter(outputFilePath, true));
def stepName = testRunner.runContext.currentStep.name
Map resultOf = testRunner.getResults()
 .collectEntries { result ->  [ (result.testStep): result.status ] }
def myStep = context.getTestCase().getTestStepByName(stepName)
def endpoint = context.expand( '${REST Request#Endpoint}' )
def request = context.expand( '${'+stepName+'#RawRequest}' )
def responseBody = context.expand( '${'+stepName+'#RawResponse}' )
responseBody = responseBody.replaceAll('"','\"')
def responseCode = context.expand( '${'+stepName+'#HarResponse#$[\'status\']}' )
def timetaken = testStepResult.getTimeTaken()
def stepStatus = resultOf[myStep]
def out = "START,"+stepName+","+endpoint+","+request+','+responseBody+","+responseCode+","+timetaken+","+stepStatus+", END"
String [] record = out.split(",")
writer.writeNext(record)
writer.close()
